function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

	//here we want to add one more chice "Development" in Choice list "u_request_type' if u_requested_for' = "jinal Patel" 

	var user=g_form.getValue("u_requested_field");
	if(user=="Jinal Patel" || user=="jinal patel")
		{
			g_form.addOption("u_request_type","Development","devlopment");   //it will add one more otion at END of the choice list
		}
   //Type appropriate comment here, and begin script below
   alert("You have changed the short description from "+ oldValue + " to "+ newValue);
}
