sys_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below
   alert("Thank you for loading NeedIt form.when you close it you will be able to interact with Form ");

	g_form.addInfoMessage("Welcome to  NeedIt Application....");
	alert("Most recent Action::"+g_form.getActionName());
	alert("value ::"+g_form.isMandatory("Short Description"));
	g_form.setDisabled("short_description",true); //use the name not label property of field in script  //this fun is used to disabled mention field
	g_form.setDisplay("description",false);  //it will hide the field if we set it to false 
	g_form.setDisplay("u_what_needed",false);  // it will hde What needed field 

	var req_type =g_form.getValue("u_request_type");
	alert("Requested Type::"+req_type);
   alert("Table Name::"+g_form.getTableName());



}
